import React, {useEffect, useState} from "react";
import {createRoot} from 'react-dom/client';
import PupCards from "./PupCards";
//this is will wrap around the app so we can use it on entire application
import { BrowserRouter } from "react-router-dom";



const PuppyMain = () => {

    //set our state as empty array with variable name players
    //setPlayers will be our function that manipulates the state of our variable players
    const [players, setPlayers] = useState([]);
    const [selectedPlayer, setselectedPlayer] = useState({});


   //get api info for players and assign it to our players state
    const getPlayers = async (setPlayers) => {
        try {
            const response = await fetch ("https://fsa-puppy-bowl.herokuapp.com/api/2211-FTB-ET-WEB-FT/players");
            const data = await response.json();
            setPlayers(data.data.players);
        } catch (error){
        console.error('oops! Trouble fetching players', error);
    };
    //this is not ideal but we needed to target the specific data and we were getting back data that was too shallow



    const getSinglePlayer = async (setSelectedPlayer, playerId) => {
        try {
            const response = await fetch (`https://fsa-puppy-bowl.herokuapp.com/api/2211-FTB-ET-WEB-FT/${playerId}`); //use variable names
            const data = await response.json();
            setselectedPlayer(data.data.players);
            if (data.error) {
                throw data.error;
            }
            return data.data.player
          } catch (error) {
            console.error('Uh oh, trouble fetching a player!', error);
        }
    };





    };
     useEffect(() => {
      getPlayers(setPlayers);

    }, []);






    // will likely need to define one pup so that we can iterate through our array and
    return( 
        <div id="all-players-container">
           {/* calling our component Pupcards and passing in our backpack of pupdata */}
                {<PupCards players = {players} 
                        getSinglePlayer = {getPlayers}/>} 
         </div>);


};

const appElement = document.getElementById("app");
const root = createRoot(appElement);

root.render(

<PuppyMain />

);
