import React from 'react';





//props is our backpack that we put our pupData into in index.js - we called it players
const PupCards = (props) => {
    console.log(props);


//create a variable that will hold our mapped players
                //go into backpack, pull out our folder called players, map through and create each player
const PupsToMap = props.players.map((player) => {
           //return all of this data for each player 
        return(
               <div className="single-player-card">
            <div className="header-info">
                                        {/* {//*change the access point to just player instead of props.players//} */}
                <p className="pup-title">{player.name}</p>
                <p className="pup-number">#{player.id}</p>
            </div>
            <img src={player.imageUrl} />
            <button className="detail-button" data-id={player.id}>See details</button>
            <button className="delete-button" data-id={player.id}>Remove from roster</button>
            </div>  
            );
        });
        //we need to put all of the data we just created as pupstomap into a new div
        return (
            <div id = "PupContainer">
                {PupsToMap}
            </div>)
};



export default PupCards;
