import React, {useEffect, useState} from "react";
import {ReactDOM} from "react";
import {createRoot} from 'react-dom/client';
import { renderAllPlayers } from "./renderHelpers";


const PuppyMain = () => {

    const [players, setPlayers] = useState([]);

    const getPlayers = async () => {
    const response = await fetch ("https://fsa-puppy-bowl.herokuapp.com/api/2211-FTB-ET-WEB-PT/players");
    const players = await response.json();
    setPlayers(players);
    console.log(players);
    };

    useEffect(() => {
      getPlayers();
    }, []); 


    return( 
        <div id="all-players-container">
            <div class="single-player-card">
            <div class="header-info">
            <p class="pup-title">{players.pup.name}</p>
            <p class="pup-number">{players.pup.id}</p>
            </div>
          <img src="{pup.imageUrl}" alt="photo of {pup.name} the puppy"/>
          <button class="detail-button" data-id={players.pup.id}>See details</button>
          <button class="delete-button" data-id={players.pup.id}>Remove from roster</button>
        </div>
        </div>);

};

const appElement = document.getElementById("app");
const root = createRoot(appElement);

root.render(<PuppyMain />);
