import React from 'react';
import { pupData } from './pupData';

//props is our backpack to put our pupcard into 
const PupCard = (props) => {
    //put pup data into our backpack
    //may need to define single pup for mapping through all the puppies
    const {pupData} = props;

    for(let i = {pupData}; i < {pupData}.length; i ++){
    return(
    //copied the html from renderHelpers for a single card and edited how sean did in lecture
        <div className="single-player-card">
            <div className="header-info">
                <p className="pup-title">{pupData[i].name}</p>
                <p className="pup-number">#{pupData[i].id}</p>
            </div>
            <img src={pupData[i].imageUrl} />
            <button className="detail-button" data-id={pupData[i].id}>See details</button>
            <button className="delete-button" data-id={pupData[i].id}>Remove from roster</button>
        </div> 
    );}
};

export default PupCard;
